catalog_ui_policy.script_false:
function onCondition() {
	var enableFields = [
		'license_type',
		'business_ask_justification',
		'worker_type',
		'approving_manager',
		'request_type',
		'requested_for'
	];
	var disableFields = [
		'name_of_employee',
		'p_email_address'
	];
		for(var i = 0; i<enableFields.length;i++){
		g_form.setMandatory(enableFields[i],true);
		g_form.setVisible(enableFields[i],true);
	}
		for(var n = 0; n<disableFields.length;n++){
		g_form.setMandatory(disableFields[n],false);
		g_form.setVisible(disableFields[n],false);
	}
}

catalog_ui_policy.script_true:
function onCondition() {
	var disableFields = [
		'other_license_types',
		'mobile_license_type',
		'specific_access_to_be_disabled',
		'license_type',
		'business_ask_justification',
		'worker_type',
		'approving_manager',
		'mmc_roles',
		'grunau_roles',
		'type_of_disabled',
		'request_type',
		'requested_for'
	];
	var enableFields = [
		'name_of_employee',
		'p_email_address'
	];
	g_form.clearValue('company');
		for(var i = 0; i<enableFields.length;i++){
		g_form.setVisible(enableFields[i],true);
		g_form.setMandatory(enableFields[i],true);
	}
		for(var n = 0; n<disableFields.length;n++){
		g_form.setVisible(disableFields[n],false);
		g_form.setMandatory(disableFields[n],false);
	}
}
